import React from 'react'

class AltCheck extends React.Component {
  constructor(props) {
    super(props)
    this.className = `altcheck-${Math.random().toString(36).slice(2)}`
  }

  render() {
    const { className } = this
    const { dev } = this.props

    // default to 'On' for now
    if (dev === true || dev === undefined) {
      return (
        <div className={className}>
          {this.props.children}
          <style>
            {`
              .${className} img:not([alt]) {
                border: 2px solid red !important;
              }
            `}
          </style>
        </div>
      )
    }

    return this.props.children
  }
}

export default AltCheck
